// Variables
var accessToken = "8a23b62be57f056609e81a244648a257a284d043";
var deviceID = "47004c001051373331333230";
var APIFunction = "LCDPrint";
var url = "https://api.particle.io/v1/devices/" + deviceID + "/" + APIFunction + "?" + "access_token=" + accessToken;



// Functions
function APICallback(data, status){
	if (data.return_value == 1) {
		alert("Your message has been sent.");
	}
	else if (data.return_value == -1) {
		alert("Error with Message POST. Particle Cloud API return value error.");
	}
	else {
	    alert("Could not connect to Particle Cloud API. Please try again later.");
	}
	
}

function sendData(){
	message = $("#message").val();
   	$.post(url, {params: message}, APICallback);
   	
   	clearMessage();
   	
   	
}   	

function clearMessage() {
    
    document.getElementById("message").value = "";
    
}