var deviceID = "47004c001051373331333230";
var accessToken = "6f3236f2330f9d2cf18c2c986897b99d6c08cc30";
var url = "https://api.particle.io/v1/devices/"+deviceID+"/led";

function callback(data, status) {
    if (data.return_value == 1) {
        alert("LED has been set to On");
    }
    else if (data.return_value == 8) {
        alert("LED has been set to Off");
    }
    else {
        alert("Unknown error");
    }
}


function toggleOff() {
    $.post(url, {params: "off", access_token: accessToken});
}

function toggleOn() {
    $.post(url, {params: "on", access_token: accessToken});
}
